export interface EventData {
    type: string;
    ts: number;
    data?: any;
}
export interface EventHandler {
    (eventData: EventData, ...args: any[]): any;
}
export interface EBus {
    on(type: string, handler: EventHandler): any;
    off(type: string, handler?: EventHandler): any;
    emit(type: string, data: any): any;
}
